  `file_id` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_german2_ci DEFAULT NULL COMMENT '头像地址',
  `user_type` tinyint(1) DEFAULT 1 COMMENT '用户类型：1-系统用户，2-客户端用户',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态 1-正常 2-禁用(默认为1)',
  `create_by` bigint(20) DEFAULT NULL COMMENT '创建人',
  `deletec` tinyint(1) DEFAULT 0 COMMENT '是否删除0-未删除，1-删除（默认为0）',
  `create_time` datetime(0) DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间（默认为CURRENT_TIMESTAMP）',
  `update_time` datetime(0) DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间（默认为CURRENT_TIMESTAMP）',
  `remark` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_german2_ci DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `uniq_index`(`user_name`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_german2_ci ROW_FORMAT = Compact;

